const e=JSON.parse('{"key":"v-150edbbb","path":"/%E5%A4%A7%E5%89%8D%E7%AB%AF/%E6%96%B9%E6%A1%88%E8%90%BD%E5%9C%B0/%E9%A1%B5%E9%9D%A2%E6%90%9C%E7%B4%A2.html","title":"页面搜索","lang":"zh-CN","frontmatter":{"description":"页面搜索 原理 页面搜索 是复杂后台系统非常常见的一个功能，它可以在指定搜索框中对当前应用中所有页面进行检索，以 select 的形式展示出被检索的页面，以达到快速进入的目的 核心点 根据指定内容对所有页面进行检索 以 select 形式展示检索出的页面 通过检索页面可快速进入对应页面 方案 创建 headerSearch 组件，用作样式展示和用户输入内容获取 获取所有的页面数据，用作被检索的数据源 根据用户输入内容在数据源中进行 模糊搜索 把搜索到的内容以 select 进行展示 监听 select 的 change 事件，完成对应跳转","head":[["meta",{"property":"og:url","content":"https://mister-hope.github.io/Lin-Note-v3/%E5%A4%A7%E5%89%8D%E7%AB%AF/%E6%96%B9%E6%A1%88%E8%90%BD%E5%9C%B0/%E9%A1%B5%E9%9D%A2%E6%90%9C%E7%B4%A2.html"}],["meta",{"property":"og:site_name","content":"Lin-Note"}],["meta",{"property":"og:title","content":"页面搜索"}],["meta",{"property":"og:description","content":"页面搜索 原理 页面搜索 是复杂后台系统非常常见的一个功能，它可以在指定搜索框中对当前应用中所有页面进行检索，以 select 的形式展示出被检索的页面，以达到快速进入的目的 核心点 根据指定内容对所有页面进行检索 以 select 形式展示检索出的页面 通过检索页面可快速进入对应页面 方案 创建 headerSearch 组件，用作样式展示和用户输入内容获取 获取所有的页面数据，用作被检索的数据源 根据用户输入内容在数据源中进行 模糊搜索 把搜索到的内容以 select 进行展示 监听 select 的 change 事件，完成对应跳转"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2024-04-21T16:39:25.000Z"}],["meta",{"property":"article:modified_time","content":"2024-04-21T16:39:25.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"页面搜索\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2024-04-21T16:39:25.000Z\\",\\"author\\":[]}"]]},"headers":[{"level":2,"title":"原理","slug":"原理","link":"#原理","children":[]},{"level":2,"title":"核心点","slug":"核心点","link":"#核心点","children":[]},{"level":2,"title":"方案","slug":"方案","link":"#方案","children":[]},{"level":2,"title":"具体实现","slug":"具体实现","link":"#具体实现","children":[]}],"git":{"createdTime":1713717565000,"updatedTime":1713717565000,"contributors":[{"name":"Jack_Lin","email":"1351123861@qq.com","commits":1}]},"readingTime":{"minutes":0.76,"words":229},"filePathRelative":"大前端/方案落地/页面搜索.md","localizedDate":"2024年4月21日","excerpt":"<h1> 页面搜索</h1>\\n<h2> 原理</h2>\\n<p><strong>页面搜索</strong> 是复杂后台系统非常常见的一个功能，它可以在指定搜索框中对当前应用中所有页面进行检索，以 select 的形式展示出被检索的页面，以达到快速进入的目的</p>\\n<h2> 核心点</h2>\\n<ol>\\n<li>根据指定内容对所有页面进行检索</li>\\n<li>以 select 形式展示检索出的页面</li>\\n<li>通过检索页面可快速进入对应页面</li>\\n</ol>\\n<h2> 方案</h2>\\n<ol>\\n<li>创建 <code>headerSearch</code> 组件，用作样式展示和用户输入内容获取</li>\\n<li>获取所有的页面数据，用作被检索的数据源</li>\\n<li>根据用户输入内容在数据源中进行 <strong>模糊搜索</strong></li>\\n<li>把搜索到的内容以 <code>select</code> 进行展示</li>\\n<li>监听 <code>select</code> 的 <code>change</code> 事件，完成对应跳转</li>\\n</ol>","autoDesc":true}');export{e as data};
